    const editInfo = (info) => {
      // alert("thông tin bố nhận được" + info)
      setNoteEditOjbect(info);
      // hàm lấy thông tin xóa

      }
      const deleteData = (id) => {
        // alert("id bố nhận được" + id);
        const db = getDatabase();
        var data=ref(db,'Note/');  
        remove(child(data,id));
  alert('đã xóa dữ liệu co id là: ' + id);
    }

    //hàm đổi trạng thái false - true
    const trangthaiNut1 = () => {
      setStatus(true);
    }
  const activeForm = () => {
    if (status === true) {
      return(
        <NoteAdd getData = {(info) =>getData(info)}/>
      )
    }
    else
      return(
      <NoteEdit fstatusNut = {() => trangthaiNut1()}
                noteEditObject = {noteEditObject}
      />
      )
  }

  return (
    <div>
      <Menu/>
      <div className='container'>
        <div className='row'>
          <NoteList fstatusNut = {() => trangthaiNut0()}
                    edit = {(info) => editInfo(info)}
                    deleteInfo = {(id) => deleteData(id)}
          />
          {activeForm()}

        </div>
      </div>
    </div>
  );
}

export default App;
