import React, { Component } from 'react'

export default class NoteEdit extends Component {

    constructor(props, context) {
        super(props, context);
        this.state={
            id:this.props.noteEditObject
        }
    }
    
    isChange =(event) =>{
        const name = event.target.name;
        const value = event.target.value;
    }
  render() {
    return (
        <div className="col-sm-3">
        <h3>Sửa thông tin</h3>
        <div className="form-group">
          <label htmlFor="noteTitle">Tiêu đề Note</label>
          <input defaultValue={this.props.noteEditObject.noteTitle} type="text" className="form-control" name="noteTitle" id="noteTitle" aria-describedby="helpId" placeholder="Tiêu đề Note" />
        </div>
        <div className="form-group">
          <label htmlFor="noteContent">Nội dung Note</label>
          <textarea defaultValue={this.props.noteEditObject.noteContent} className="form-control" name="noteContent" id="noteContent" rows={3} />
        </div>
        <div className='text-center'>
            <button onClick={()=>this.props.fstatusNut()} type="submit" className="btn btn-secondary mr-2">Hủy</button>
            <button type="submit" className="btn btn-secondary">Lưu</button>
        </div>
      </div>
    )
  }
}
