import { useState } from "react";
import '../App.css';
import Menu from './Menu';
import NoteAdd from './NoteAdd';
import NoteList from './NoteList';
import {dataNote} from './connectfb'
import { push } from 'firebase/database';
import NoteEdit from './NoteEdit';

const getData = (info) =>{
  alert('thông tin bố nhận được '+ info);
  push(dataNote,info);
}

// hàm lấy thông tin cần sửa
const editInfo=()=>{
  alert("thông tin bố mày nhận được"+info)
}
function App() {
  //console.log(connect);
  //console.log(dataNote);
  // khai báo state điều khiển thay đổi form
  const[status, setStatus] = useState(true);

  // hàm đổi trạng thái true -> false

  const trangthaiNut0=()=>{
    setStatus(false);
  }
  // hàm đổi trạng thái   false->true

  const trangthaiNut1=()=>{
    setStatus(true);
  }
  const activeForm = ()=>{
    if (status===true) {
      return(
        <NoteAdd getData={(info)=> getData(info)}></NoteAdd>

      )
    }
    else return(<NoteEdit fstatusNut = {()=> trangthaiNut1()}></NoteEdit>

    )

  }
  return (
    <div>
      <Menu></Menu>
      <div class="container">
        <div class="row">
          <NoteList fstatusNut = {(info)=> trangthaiNut0(info)}
            edit= {()=>editInfo()}
          ></NoteList>
          {activeForm()}
          </div>
        </div>
    </div>
  );
}

export default App;
