import React, { Component } from 'react'
import NoteItem from './NoteItem'
import {dataNote} from './connectfb'
import { onValue } from 'firebase/database';
export default class NoteList extends Component {
  //conc -> tab
  constructor(props, context) {
    super(props, context);
    this.state={dataFirebase:[]}
  }
  //cwm
  componentWillMount() {
    onValue(dataNote, (snapshot) => {
      // const notes = snapshot.val();
      // console.log(notes);
      var arrayData=[];
      snapshot.forEach(function (childSnapshot) {
        var key = childSnapshot.key;
        var noteTitle = childSnapshot.val().noteTitle;
        var noteContent = childSnapshot.val().noteContent;
        //console.log(noteTitle);
        arrayData.push({
          id: key,
          noteTitle: noteTitle,
          noteContent: noteContent
        })
        
      })
      this.setState({dataFirebase:arrayData})
      
    })
  }
  
  getData=() =>{
    //log xem noteList cos data ko
    //console.log(this.state.dataFirebase);
    if(this.state.dataFirebase){
      return this.state.dataFirebase.map((value,key)=>{
        return(
          <NoteItem
            key={key}
            i={key}
            noteTitle={value.noteTitle}
            noteContent={value.noteContent}
            fstatusNut={()=>this.props.fstatusNut()}
            editFunClick={(info)=>this.props.edit(value)}
            
            ></NoteItem>
        )
      })
    }
  }
  
  render() {
   // console.log(dataNote);
    return (
        <div className="col-9">
        <div id="accordianId" role="tablist" aria-multiselectable="true">
          {/* <NoteItem></NoteItem> */}
          {this.getData()}
        </div>
      </div>
      
    )
  }
}
